('junior', 'Form', 'https://git-react-components.s3.amazonaws.com/d72d9a31-902f-4eab-bc89-fe9738950eac-Login-Form.PNG', 'Login Form', '{  "html":"<html lang=en>\n\n<meta charset=\"utf-8\">\n<meta name=viewport content=\"width=device-width, initial-scale=1\">\n<title>form</title>\n<body>\n  \n<main id=component></main>\n\n<script src=\"https://unpkg.com/react@15/dist/react.min.js\"></script>\n<script src=\"https://unpkg.com/react-dom@15/dist/react-dom.min.js\"></script>\n\n</body>\n</html>",  "scss":":root\n{\n\t--white: #fff;\n\t--mineshaft: #333333;\n\t--carnation: #fa5c58;\n\t--gray: #808080;\n\t--aquahaze: #f1f4f7;\n\t--montserrat: \"MontserratRegular\", sans-serif;\n\t--lora: \"LoraRegular\", serif;\n}\n\ninput, textarea\n{\n\tmargin: o;\n\tpadding: 0;\n\tborder: none;\n\tbox-shadow: none;\n\toutline: none;\n}\n\n#component {\n  height: 100vh;\n  display: flex;\n  justify-content: center;\n}\n\n.send-message {\n  width: 50%;\n  max-height: 300px;\n  display: flex;\n  flex-flow: row wrap;\n  font-family: var(--montserrat);\n  text-transform: uppercase;\n}\n\n.send-message > * {\n    height: 20px;\n    margin: 5px;\n    padding: 5px 15px;\n    flex: 1 100%;\n    text-transform: capitalize;\n    font-size: 15px;\n    color: var(--gray);\n    background: var(--aquahaze);\n}\n\n.send-message > *:focus {\n      border: 1px solid var(--mineshaft);\n}\n\n.login, .password {\n    flex: 1 auto;\n}\n\n.message-text {\n    height: 100px;\n    color: var(--gray);\n}\n\n.send {\n    height: 40px;\n    text-transform: uppercase;\n    font-size: 18px;\n    color: var(--white);\n    background: var(--carnation);\n}\n\n.send:hover {\n      color: var(--carnation);\n      background: var(--white);\n      border: 2px solid var(--carnation);\n      cursor: pointer;\n}",  "js":"class DemoComponent extends React.Component {\n  constructor(...args) {\n    super(...args)\n    this.state = {\n      login: \"\",\n      password: \"\",\n      showPassword: false,\n      messageHead: \"\",\n      massageText: \"\"\n    }\n    \n    this.changeLogin = this.changeLogin.bind(this)\n    this.changePassword = this.changePassword.bind(this)\n    this.changeShowPassword = this.changeShowPassword.bind(this)\n    this.changeMessageHead = this.changeMessageHead.bind(this)\n  }\n  \n  changeLogin(event) {\n    this.setState({ login: event.target.value })\n  }\n  \n  changePassword(event) {\n    this.setState({ password: event.target.value })\n  }\n  \n  changeShowPassword(event) {\n    this.setState({ showPassword: event.target.checked })\n  }\n  \n  changeMessageHead(event) {\n    this.setState({ messageHead: event.target.value })\n  }\n  \n  changeMessageText(event) {\n    this.setState({ messageText: event.target.value })\n  }\n  \n  render() {\n    return React.createElement(\n      \"form\",\n      {\n        className: \"send-message\"\n      }, //or null\n      React.createElement(\n        \"input\",\n        { \n          type: \"text\", \n          placeholder: \"Login\", \n          value: this.state.login, \n          onChange: this.changeLogin,\n          className: \"login\"\n        }\n      ),\n      React.createElement(\n        \"input\",\n        { \n          type: this.state.showPassword ? \"text\" : \"password\", \n          placeholder: \"Password\", \n          value: this.state.password, \n          onChange: this.changePassword,\n          className: \"password\"\n        }\n      ),\n      React.createElement(\n        \"label\",\n        {},\n        React.createElement(\n          \"input\",\n          { \n            type: \"checkbox\",\n            value: this.state.showPassword,\n            onChange: this.changeShowPassword \n          }\n        ),\n        \"Show password\"\n      ),\n      React.createElement(\n        \"input\",\n        { \n          type: \"text\", \n          placeholder: \"Type message here...\", \n          value: this.state.messageHead,\n          onChange: this.changeMessageHead\n        }\n      ),\n      React.createElement(\n        \"textarea\",\n        { \n          placeholder: \"Message\", \n          value: this.state.messageText, \n          onChange: this.changeMessageText,\n          className: \"message-text\"\n       }\n      ),\n      React.createElement(\n        \"input\",\n        {\n          type: \"submit\",\n          value: \"send\",\n          className: \"send\"\n        }\n      )\n    )\n  }\n}\n\nReactDOM.render (\n  React.createElement(\n    DemoComponent,\n    { name: \"Vasya\" }\n  ), \n  document.getElementById(\"component\")\n)"}'),
('junior', 'Form', 'https://git-react-components.s3.amazonaws.com/52fc6dcc-8075-47b3-82f6-071524654445-Login-Form-with-Social-Media.PNG', 'Login Form with Social Media', '{  "html":"#root\n// Password: test",  "scss":"// Variables\n$color-bg: white;\n$color-main: #333;\n$color-hover: lighten(#0275d8, 1%);\n$padding: 20px;\n$radius: 5px;\n\n// Mixins\n@mixin transition($prop: all, $duration: 0.3s, $timing: ease-in-out) {\n  transition: $prop $duration $timing;\n}\n\nhtml {\n  box-sizing: border-box;\n}\n*,\n*:after,\n*:before {\n  box-sizing: border-box;\n}\nbody {\n  font-family: \"Roboto\", sans-serif;\n}\n#root {\n  background: url(\"https://source.unsplash.com/tN7fJdTaU40/1600x1200\") no-repeat center / cover;\n  display: flex;\n  align-items: center;\n  justify-content: center;\n  position: absolute;\n  height: 100%;\n  width: 100%;\n}\n.react-form {\n  width: 300px;\n  border-radius: $radius;\n  background: $color-bg;\n  box-shadow: 0 15px 35px rgba(50, 50, 93, 0.1), 0 5px 15px rgba(0, 0, 0, 0.07);\n  padding: $padding;\n  text-align: center;\n}\nh1 {\n  color: $color-main;\n  font-size: 24px;\n  margin-top: 0;\n}\ninput {\n  border: none;\n  border-bottom: 1px solid $color-main;\n  background: none;\n  width: 100%;\n  font-size: 18px;\n  font-weight: bold;\n  line-height: 1.2;\n  outline: none;\n}\n.btn:hover {\n  background: $color-hover;\n  border-color: $color-hover;\n}\n",  "js":"const {\n  HashRouter,\n  Switch,\n  Route\n} = ReactRouterDOM\n\nconst App = () => (\n  <Switch>\n    <Route exact path=''/'' component={ReactForm}/>\n    <Route path=''/success'' component={Success}/>\n  </Switch>\n)\n\nclass ReactLabel extends React.Component {\n  constructor() {\n    super();\n  }\n\n  render() {\n    return <label htmlFor={this.props.htmlFor}>{this.props.title}</label>;\n  }\n}\n\nclass ReactForm extends React.Component {\n  constructor(props) {\n    super(props);\n    this.state = {\n      username: \"\",\n      password: \"\",\n      valid: true\n    };\n    this.handleSubmit = this.handleSubmit.bind(this);\n  }\n\n  handleSubmit(e) {\n  var input = this.refs.myInput;\n  var inputValue = input.value;\n    if (inputValue!==\"test\")  {\n          this.setState({\n            valid: false\n          });\n    } else {\n     this.state.valid && this.props.history.push(''/success''); \n    }\n    \n  }\n\n  render() {\n    let formClass = \"react-form\";\n    (!this.state.valid) && (formClass += \" error\");\n    return (\n      <form className={formClass} onSubmit={this.handleSubmit}>\n        <fieldset className=\"form-group\">\n          <ReactLabel htmlFor=\"formName\" title=\"Username:\" />\n          <input            \n            id=\"formName\"\n            className=\"form-input\"\n            name=\"username\"\n            type=\"text\"\n          />\n        </fieldset>\n        <fieldset className=\"form-group\">\n          <ReactLabel htmlFor=\"formPassword\" title=\"Password:\" />\n          <input\n            id=\"formPassword\"\n            ref=\"myInput\"\n            className=\"form-input\"\n            name=\"password\"\n            type=\"text\"\n          />\n        </fieldset>\n        {(!this.state.valid) && <p className=\"alert-danger\">Please try again</p>}\n        <input\n          id=\"formButton\"\n          className=\"btn btn-primary\"\n          type=\"submit\"\n          placeholder=\"Send message\"\n        />\n      </form>\n    );\n  }\n}\n\nconst Success = () => (\n  <h2>Thank you!</h2>\n)\n\nReactDOM.render((\n  <HashRouter>\n    <App />\n  </HashRouter>\n), document.getElementById(''root''))"}');
('junior', 'Form', 'https://git-react-components.s3.amazonaws.com/b58ce481-f3f8-4dff-8238-ae6885c0e803-Simple-Form.PNG', 'Simple Form', '{  "html":"<div id=\"app\"></div>  ",  "scss":"@import ''https://fonts.googleapis.com/css?family=Noto+Sans''\n\n// Font variables\n$main-font: ''Noto Sans'', sans-serif\n\n// Color variables\n$darkblue: rgba(40, 75, 99, 0.8)\n$fadedwhite: rgba(255,255,255,.7)\n$accentcolor: #58a4b0\n$fbcolor: #3A5A98\n$twcolor: #55ACEE\n\nbody\n  background: radial-gradient(ellipse farthest-corner at center top, #d8dbe2 0%, #a9bcd0 100%)\n  height: 100vh\n  font-family: $main-font\n  background: url(''https://hd.unsplash.com/photo-1435777940218-be0b632d06db'') no-repeat center center fixed\n  \n// App style  \n.Modal\n  position: absolute\n  top: 50%\n  left: 50%\n  transform: translate(-50%, -50%)\n  background: $darkblue\n  background-size: cover\n  border-radius: 5px\n  // Lost password style\n  & a\n    display: block\n    color: $fadedwhite\n    font-size: 12px\n    text-align: center\n    text-decoration: none\n    margin: 4px 0 10px 0\n    margin: 10px\n    &:hover\n      text-decoration: underline\n\n// Input  \n.Input\n  display: flex\n  // Hack to be able to have a label pseudo element before the input field\n  flex-direction: row-reverse\n  \n  align-items: center\n  border-bottom: 1px solid #58a4b0\n  margin: 0 20px\n  & input\n    border: 0\n    background: transparent\n    padding: 2px 10px\n    margin: 18px 4px 0 4px\n    color: $fadedwhite\n    font-family: $main-font\n    font-size: 14px\n    \n    &:required\n      box-shadow: none\n    \n    // Animation trigger for label::before\n    &:focus ~ label\n      opacity: 1\n    \n    // label::before icons\n    &[type=''text'']\n      ~ label\n        &::before\n          content: \"\f007\"\n    &[type=''password'']\n      ~ label\n        &::before\n          content: \"\f023\"\n      \n// Label\nlabel\n  font-family: FontAwesome\n  color: white\n  font-size: 14px\n  margin-top: 17px\n  transition: opacity .3s ease-in-out\n  // pseudo element before input field (see hack above)\n  &::before\n    padding: 5px 5px 0 0\n    opacity: 0.5\n\n// Buttons\n\n// Sign in button\nform \n  & button\n    position: relative\n    margin: 15px 0 5px 0\n    left: 50%\n    transform: translateX(-50%)\n    background: $accentcolor\n    border: none\n    border-radius: 5px\n    padding: 10px\n    color: white\n    width: 85%\n    font-family: $main-font\n    font-size: 14px\n    cursor: pointer\n    \n// Social login buttons    \n.social-signin\n  text-align: center\n  & button\n    width: 40%\n    border: none\n    border-radius: 5px\n    cursor: pointer\n    padding: 10px\n    & i\n      font-size: 20px\n      color: white\n    &.fb\n      background: $fbcolor\n      margin-right: 13px\n    &.tw\n      background: $twcolor\n// modal back button\n.bringitback\n  position: absolute\n  left: 50%\n  top: 50%\n  transform: translateX(-50%)\n  background: $accentcolor\n  border: none\n  border-radius: 5px\n  padding: 10px\n  color: white\n  width: 200px\n  font-family: $main-font\n  font-size: 14px\n  cursor: pointer \n\n// Fake logo\n.logo\n  text-align: center\n  color: white\n  & i\n    display: block\n    padding: 20px 0 0 0 \n    font-size: 70px\n    transform: translateX(-5px)\n  & span\n    color: $fadedwhite\n    text-transform: uppercase\n    \n\n// React CSS animations\n\n// On enter\n.animation-enter\n  opacity: .01\n\t\n  &.animation-enter-active\n    opacity: 1\n    transition: opacity .6s ease, margin .5s ease\n\t\n// On leave\n.animation-leave\n  margin-top: 0px\n  opacity: 1\n  &.animation-leave-active\n    margin-top: -30px\n    opacity: .01\n    transition: opacity .2s ease\n\n// On mount\n.animation-appear\n  opacity: 0.01\n.animation-appear.animation-appear-active \n  opacity: 1\n  transition: opacity .5s ease-in",  "js":"const ReactCSSTG = React.addons.CSSTransitionGroup;\n\n// Main app\nclass App extends React.Component {\n  constructor(props) {\n    super(props);\n    this.state = {\n        isVisible: true\n      }\n      // Bindings\n    this.handleSubmit = this.handleSubmit.bind(this);\n    this.handleRemount = this.handleRemount.bind(this);\n  }\n\n  handleSubmit(e) {\n    e.preventDefault();\n    this.setState({\n      isVisible: false\n    }, function() {\n      console.log(this.state.isVisible)\n    });\n    return false;\n  }\n  handleRemount(e) {\n    this.setState({\n      isVisible: true\n    }, function() {\n      console.log(this.state.isVisible)\n    });\n    e.preventDefault();\n  }\n  render() {\n\n    // const for React CSS transition declaration\n    let component = this.state.isVisible ? <Modal onSubmit={ this.handleSubmit } key=''modal''/> : <ModalBack onClick={ this.handleRemount } key=''bringitback''/>;\n\n    return <ReactCSSTG transitionName=\"animation\" transitionAppear={true} transitionAppearTimeout={500} transitionEnterTimeout={500} transitionLeaveTimeout={300}>\n             { component }\n           </ReactCSSTG>\n  }\n}\n\n// Modal\nclass Modal extends React.Component {\n  render() {\n    return <div className=''Modal''>\n              <Logo />\n              <form onSubmit= { this.props.onSubmit }>\n                <Input type=''text'' name=''username'' placeholder=''username'' />\n                <Input type=''password'' name=''password'' placeholder=''password'' />\n                <button> Sign In</button>\n              </form>\n              <div className=''social-signin''>\n                <button className=\"fb\" onClick={ this.props.onClick }><i className=\"fa fa-facebook\" aria-hidden=\"true\"></i></button>\n                <button className=\"tw\" onClick={ this.props.onClick }><i className=\"fa fa-twitter\" aria-hidden=\"true\"></i></button>\n              </div>\n                <a href=''#''>Lost your password ?</a>\n           </div>\n  }\n}\n\n// Generic input field\nclass Input extends React.Component {\n  render() {\n    return <div className=''Input''>\n              <input type={ this.props.type } name={ this.props.name } placeholder={ this.props.placeholder } required autocomplete=''false''/>\n              <label for={ this.props.name } ></label>\n           </div>\n  }\n\n}\n\n// Fake logo\nclass Logo extends React.Component {\n  render() {\n    return <div className=\"logo\">\n                <i className=\"fa fa-bug\" aria-hidden=\"true\"></i> \n                <span> awesome logo </span>\n              </div>\n  }\n}\n\n// Button to brind the modal back\nclass ModalBack extends React.Component {\n  render() {\n    return <button className=\"bringitback\" onClick={ this.props.onClick } key={ this.props.className }>Brind the modal back !</button>\n  }\n}\n\nReactDOM.render(<App />, document.getElementById(''app''));"}'),
('junior', 'Form', 'https://git-react-components.s3.amazonaws.com/138ec3e9-1fa3-4d9f-b679-ac4bb6258f6f-Survey-Form.PNG', 'Survey Form', '{  "html":"<div id=\"app\"><div>",  "scss":"*, *:before, *:after {\n  box-sizing: border-box;\n}\n\n$primary: #e14;\n$secondary: #124;\n$accent: #e72;\n\nbody {\n  font: 400 16px Montserrat, sans-serif;\n  color: #444;\n  padding: 0;\n  margin: 0;\n  background-color: #f8f8f8;\n}\n\n.container {\n  display: flex;\n  min-height: 100vh;\n  align-items: center;\n  justify-content: center;\n}\n\n.card {\n  width: 600px;\n  background-color: #fff;\n  border-radius: 3px;\n  box-shadow: 0 2px 10px rgba($secondary,.06);\n  overflow: hidden;\n}\n\n.waves {\n  height: 80px;\n  background: linear-gradient(to bottom left, $primary, $accent);\n}\n\nh1 {\n  margin: 2rem 2rem 0;\n  font-size: 1.62rem;\n}\n\n.form {\n  padding: 2rem;\n}\n\n.button {\n  display: block;\n  width: 100%;\n  background-color: #e14;\n  border: none;\n  border-radius: 3px;\n  padding: 1ch;\n  font-size: 1rem;\n  color: #fff;\n  font-family: inherit;\n}\n\n@mixin input {\n  display: block;\n  width: 100%;\n  border: 2px solid #eee;\n  padding: 1ch;\n  border-radius: 3px;\n  transition: 150ms ease;\n  outline: none;\n  font-size: 1rem;\n  font-family: inherit;\n}\n\n@mixin input-focus {\n  border-color: #e14;\n}\n\n@mixin label {\n  position: absolute;\n  top: 1ch;\n  left: 1ch;\n  padding: 0 .5ch;\n  pointer-events: none;\n  transition: 150ms ease;\n  color: #888;\n}\n\n@mixin label-focus {\n  position: absolute;\n  top: 0;\n  font-size: 0.9em;\n  color: #e14;\n  font-weight: bold;\n  transform: translateY(-50%);\n  background-color: #fff;\n  font-weight: 600;\n}\n\n\n\n.text-input {\n  position: relative;\n  margin-bottom: 1rem;\n  label {\n    @include label;\n  }\n  label.label-focus {\n    @include label-focus;\n  }\n  input {\n    @include input;\n  }\n  input.input-focus {\n    @include input-focus;\n  }\n}\n\n.text-area {\n  position: relative;\n  margin-bottom: 1rem;\n  label {\n    @include label;\n  }\n  label.label-focus {\n    @include label-focus;\n  }\n  textarea {\n    @include input;\n    height: 8em;\n  }\n  textarea.input-focus {\n    @include input-focus;\n  }\n}",  "js":"/** Components */\nconst Card = props => (\n  <div className=\"card\">\n    {/*<div className=\"waves\">\n    </div>*/}\n    {props.children}\n  </div>\n);\n\nconst Form = props => (\n  <form className=\"form\">{props.children}</form>\n);\n\nconst TextInput = props => (\n  <div\n    className=\"text-input\">\n    <label\n      className={(props.focus || props.value !== '') ? ''label-focus'' : ''}\n      htmlFor={props.name}>{props.label}</label>\n    <input\n      className={(props.focus || props.value !== '') ? ''input-focus'' : ''}\n      type=\"text\"\n      name={props.name}\n      value={props.value}\n      onChange={props.onChange}\n      onInput={props.onInput}\n      onFocus={props.onFocus}\n      onBlur={props.onBlur} />\n  </div>\n);\n\nconst TextArea = props => (\n  <div\n    className=\"text-area\">\n    <label\n      className={(props.focus || props.value !== '') ? ''label-focus'' : ''}\n      htmlFor={props.name}>{props.label}</label>\n    <textarea\n      className={(props.focus || props.value !== '') ? ''input-focus'' : ''}\n      name={props.name}\n      value={props.value}\n      onChange={props.onChange}\n      onInput={props.onInput}\n      onFocus={props.onFocus}\n      onBlur={props.onBlur} />\n  </div>\n);\n\nconst Button = props => (\n  <button\n    className=\"button\">{props.children}</button>\n);\n\n/** Root Component */\nclass App extends React.Component {\n  constructor() {\n    super();\n    this.state = {\n      name: {\n        name: ''name'',\n        label: ''Name'',\n        value: '',\n        focus: false,\n      },\n      email: {\n        name: ''email'',\n        label: ''Email'',\n        value: '',\n        focus: false,\n      },\n      message: {\n        name: ''message'',\n        label: ''Message'',\n        value: '',\n        focus: false,\n      },\n    }\n  }\n  \n  handleFocus(e) {\n    const name = e.target.name;\n    const state = Object.assign({}, this.state[name]);\n    state.focus = true;\n    this.setState({ [name]: state },()=>{console.log(state)});\n  }\n  \n  handleBlur(e) {\n    const name = e.target.name;\n    const state = Object.assign({}, this.state[name]);\n    state.focus = false;\n    this.setState({ [name]: state },()=>{console.log(state)});\n  }\n  \n  handleChange(e) {\n    const name = e.target.name;\n    const state = Object.assign({}, this.state[name]);\n    state.value = e.target.value;\n    this.setState({ [name]: state },()=>{console.log(state)});\n  }\n  \n  render() {\n    const {name, email, message} = this.state;\n    return (\n      <div className=\"container\">\n        <Card>\n          <h1>Send us a Message!</h1>\n          <Form>\n            <TextInput\n              {...name}\n              onFocus={this.handleFocus.bind(this)}\n              onBlur={this.handleBlur.bind(this)}\n              onChange={this.handleChange.bind(this)} />\n            <TextInput\n              {...email}\n              onFocus={this.handleFocus.bind(this)}\n              onBlur={this.handleBlur.bind(this)}\n              onChange={this.handleChange.bind(this)} />\n            <TextArea\n              {...message}\n              onFocus={this.handleFocus.bind(this)}\n              onBlur={this.handleBlur.bind(this)}\n              onChange={this.handleChange.bind(this)} />\n            <Button>Send</Button>\n          </Form>\n        </Card>\n      </div>\n    );\n  }\n}\n\nReactDOM.render(<App />, document.getElementById(''app''));"}'),
