('junior', 'Header', 'https://git-react-components.s3.amazonaws.com/061191bf-9fe5-42c9-86d7-df41f397c5e5-Header-Footer-Parallax-Transition.PNG', 'Header/Footer Parallax Effect', '{  "html":"<div class=\"header\">\n  <h1>Animated Fixed Header (Scroll Down)</h1>\n</div>\n<div class=\"content\">\n</div>\n\n<script class=\"cssdeck\" src=\"//cdnjs.cloudflare.com/ajax/libs/jquery/1.8.0/jquery.min.js\"></script>",  "scss":".header {\n    position: fixed;\n    top: 0;\n    left: 0;\n    width: 100%;\n    background: #cc5350;\n    color:#fff;\n    z-index: 1000;\n    height: 200px;\n    overflow: hidden;\n    -webkit-transition: height 0.3s;\n    -moz-transition: height 0.3s;\n    transition: height 0.3s;\n    text-align:center;\n    line-height:160px;\n\n}\n.header.shrink {\n    height: 100px;\n    line-height:80px;\n}\n.header h1\n{\n    font-size:30px;\n    font-weight:normal;\n    -webkit-transition: all 0.3s;\n    -moz-transition: all 0.3s;\n    transition: all 0.3s;\n}\n\n.header.shrink h1\n{\n    font-size:24px;\n    -webkit-transition: all 0.3s;\n    -moz-transition: all 0.3s;\n    transition: all 0.3s;\n}\n.content\n{\nheight:2000px;\n /*just to get the page to scroll*/\n}",  "js":"$(function(){\n var shrinkHeader = 300;\n  $(window).scroll(function() {\n    var scroll = getCurrentScroll();\n      if ( scroll >= shrinkHeader ) {\n           $(''.header'').addClass(''shrink'');\n        }\n        else {\n            $(''.header'').removeClass(''shrink'');\n        }\n  });\nfunction getCurrentScroll() {\n    return window.pageYOffset || document.documentElement.scrollTop;\n    }\n});"}'),
('junior', 'Header', 'https://git-react-components.s3.amazonaws.com/52a2b492-8c47-4d5e-ad0c-e8c6f3c5af97-Navbar-Header.PNG', 'Navbar Header', '{  "html":"<div id=\"scroll-animate\">\n  <div id=\"scroll-animate-main\">\n    <div class=\"wrapper-parallax\">\n      <header>\n        <h1>Header</h1>\n      </header>\n\n      <section class=\"content\">\n        <h1>Content</h1>\n      </section>\n\n      <footer>\n        <h1>Footer</h1>\n      </footer>\n    </div>\n  </div>\n</div>",  "scss":"#scroll-animate\n{\n  overflow: hidden;\n}\n\n#scroll-animate-main\n{\n  width: 100%;\n  left: 0;\n  position: fixed;\n}\n\n#heightPage,\n#heightScroll\n{\n  width: 10px;\n  top: 0;\n  position: absolute;\n  z-index: 99;\n}\n\n#heightPage\n{\n  left: 0;\n}\n\n#heightScroll\n{\n  right: 0;\n}\n\nheader\n{\n  width: 100%;\n  height: 100%;\n  background: url(https://raw.githubusercontent.com/hudsonmarinho/header-and-footer-parallax-effect/master/assets/images/bg-header.jpg) no-repeat 50% 50%;\n  top: 0;\n  position: fixed;\n  z-index: -1;\n}\n\nfooter\n{\n  width: 100%;\n  height: 300px;\n  background: gray;\n  bottom: -300px;\n  position: fixed;\n  z-index: -1;\n}\n\n.content\n{\n  height: 1000px;\n  min-height: 1000px;\n  background: #ededed;\n  position: relative;\n  z-index: 1;\n}\n\n.wrapper-parallax {\n  margin-top: 100%;\n  margin-bottom: 300px;\n  box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.5);\n}\n\nh1{\n  width: 100%;\n  height: 100%;\n  padding: 0;\n  margin: 0;\n  text-transform: uppercase;\n  text-align: center;\n  font-family: Helvetica;\n  font-size: 150px;\n  color: #fff;\n}\n\nheader h1{}\n\n.content h1{\n  line-height: 1000px;\n  color: #999;\n}\n\nfooter h1\n{\n  line-height: 300px;\n}\n\nheader,\nfooter,\n#scroll-animate-main\n{\n  -webkit-transition-property: all;\n     -moz-transition-property: all;\n      transition-property: all;\n\n  -webkit-transition-duration: 0.4s;\n     -moz-transition-duration: 0.4s;\n      transition-duration: 0.4s;\n\n  -webkit-transition-timing-function: cubic-bezier(0, 0, 0, 1);\n     -moz-transition-timing-function: cubic-bezier(0, 0, 0, 1);\n      transition-timing-function: cubic-bezier(0, 0, 0, 1);\n}",  "js":"function scrollFooter(scrollY, heightFooter)\n{\n    console.log(scrollY);\n    console.log(heightFooter);\n\n    if(scrollY >= heightFooter)\n    {\n        $(''footer'').css({\n            ''bottom'' : ''0px''\n        });\n    }\n    else\n    {\n        $(''footer'').css({\n            ''bottom'' : ''-'' + heightFooter + ''px''\n        });\n    }\n}\n\n$(window).load(function(){\n    var windowHeight        = $(window).height(),\n        footerHeight        = $(''footer'').height(),\n        heightDocument      = (windowHeight) + ($(''.content'').height()) + ($(''footer'').height()) - 20;\n\n    // Definindo o tamanho do elemento pra animar\n    $(''#scroll-animate, #scroll-animate-main'').css({\n        ''height'' :  heightDocument + ''px''\n    });\n\n    // Definindo o tamanho dos elementos header e conteÃºdo\n    $(''header'').css({\n        ''height'' : windowHeight + ''px'',\n        ''line-height'' : windowHeight + ''px''\n    });\n\n    $(''.wrapper-parallax'').css({\n        ''margin-top'' : windowHeight + ''px''\n    });\n\n    scrollFooter(window.scrollY, footerHeight);\n\n    // ao dar rolagem\n    window.onscroll = function(){\n        var scroll = window.scrollY;\n\n        $(''#scroll-animate-main'').css({\n            ''top'' : ''-'' + scroll + ''px''\n        });\n        \n        $(''header'').css({\n            ''background-position-y'' : 50 - (scroll * 100 / heightDocument) + ''%''\n        });\n\n        scrollFooter(scroll, footerHeight);\n    }\n});"}'),
('junior', 'Header', 'https://git-react-components.s3.amazonaws.com/a90030a7-9988-4b76-89e4-8a487f9f7385-Simple-header-main-footer.PNG', 'Simple header-main-footer', '{  "html":"<div id=''app''></div>",  "scss":"body {\n    margin: 0;\n}\n\nmain {\n    margin: 16px;\n}\n\n.navbar {\n    display: flex;\n    align-items: center;\n    \n    background: rebeccapurple;\n    color: white;\n    font-family: Helvetica;\n    font-weight: 300;\n}\n\n.navbar__title {\n    margin-right: auto;\n    font-size: 150%;\n    padding: 12px 16px;\n}\n\n.navbar__item {\n    padding: 16px 16px;\n    cursor: pointer;\n    vertical-align: middle;\n}",  "js":"const NavBar = () => (\n    <header className=''navbar''>\n        <div className=''navbar__title navbar__item''>Cutco</div>\n        <div className=''navbar__item''>About Us</div>\n        <div className=''navbar__item''>Contact</div>\n        <div className=''navbar__item''>Help</div>        \n    </header>\n);\n\nconst App = () => (\n    <div>\n        <NavBar />\n        <main>We sell cutting edge knives.</main>\n    </div>\n);\n\nReactDOM.render(<App />, document.getElementById(''app''));\n"}'),
('junior', 'Header', 'https://git-react-components.s3.amazonaws.com/6c07989a-abcc-42d6-9447-f219800f247e-Sticky-Header-CSS-Transition.PNG', 'Sticky Header CSS Transition', '{  "html":"<div id=\"react\"></div>",  "scss":"*,\n*:before,\n*:after {\n  box-sizing: border-box;\n}\n\nhtml {\n  font-size: 100%;\n}\n\nhtml,\nbody {\n  min-height: 100%;\n  min-height: 100vh;\n}\n\nbody {\n  font: 16px ''Roboto'', Helvetica, sans-serif;\n  display: flex;\n  flex-direction: column;\n  margin: 0;\n  background: #f6f6f6;\n}\n\n.app {\n  max-width: 60rem;\n  width: 100%;\n  background: lightgray;\n  padding: 1rem;\n  font-size: 1rem;\n  display: flex;\n  flex-direction: column;\n  justify-content: center;\n  align-items: center;\n  height: 100%;\n  min-height: 100%;\n  min-height: 100vh;\n  margin: 0 auto;\n  > header,\n  > main,\n  > footer {\n    width: 100%;\n    padding: 1rem;\n    margin: .5rem 1rem;\n    text-align: center;\n    transition: 200ms;\n    &:hover {\n      box-shadow: 0 0 2rem gray;\n    }\n  }\n  > header {\n    display: block;\n    color: white;\n    background: gray;\n    text-transform: uppercase;\n  }\n  > main {\n    flex: 1 1 auto;\n    color: white;\n    background: #333;\n  }\n  > footer {\n    background: white;\n  }\n}",  "js":"const Header = ({title}) => (<header>{title}</header>);\nconst Main = ({title}) => (<main>{title}</main>);\nconst Footer = ({title}) => (<footer>{title}</footer>);\n\nclass App extends React.Component {\n  render() {\n    const {header,main,footer} = this.props;\n    return (\n      <div className=\"app\">\n        <Header title={header} />\n        <Main title={main} />\n        <Footer title={footer}/>\n      </div>\n    );\n  }\n};\n\nReactDOM.render(\n  <App \n    header=\"I am the header\" \n    main=\"I am the main\" \n    footer=\"I am the footer\" />, \n  document.getElementById(''react'')\n);"}'),
('junior', 'List', 'https://git-react-components.s3.amazonaws.com/9024ee21-aff8-4fdb-a7b1-97c31993fe77-Drag-&-Drop-List.PNG', 'Drag & Drop List', '{  "html":"<h1>Drag &amp; Drop</h1>\n<div id=\"app\"></div>",  "scss":"body {\n  width: 100%;\n  max-width: 380px;\n  margin: 0 auto;\n}\n\nh1 {\n  border-left: 5px solid #e1e1e1;\n  padding-left: 5px;\n}\n\nul {\n  list-style: none;\n  margin:0;\n  padding:0;\n  border: 5px solid #e1e1e1;\n  box-shadow: 1px 3px 8px #888; \n}\nli {\n  padding: 10px 15px;\n  background:#eee;\n  &:hover {\n    background: darken(#eee, 5%);\n  }\n}\n\n.placeholder {\n  background: rgb(255,240,120);\n  &:before {\n    content: \"Drop here\";\n    color: rgb(225,210,90);\n  }\n}",  "js":"var placeholder = document.createElement(\"li\");\nplaceholder.className = \"placeholder\";\n\nclass List extends React.Component {\n  constructor(props) {\n    super(props);\n    this.state = {...props};\n  }\n  dragStart(e) {\n    this.dragged = e.currentTarget;\n    e.dataTransfer.effectAllowed = ''move'';\n    e.dataTransfer.setData(''text/html'', this.dragged);\n  }\n  dragEnd(e) {\n    this.dragged.style.display = ''block'';\n    this.dragged.parentNode.removeChild(placeholder);\n    \n    // update state\n    var data = this.state.colors;\n    var from = Number(this.dragged.dataset.id);\n    var to = Number(this.over.dataset.id);\n    if(from < to) to--;\n    data.splice(to, 0, data.splice(from, 1)[0]);\n    this.setState({colors: data});\n  }\n  dragOver(e) {\n    e.preventDefault();\n    this.dragged.style.display = \"none\";\n    if(e.target.className === ''placeholder'') return;\n    this.over = e.target;\n    e.target.parentNode.insertBefore(placeholder, e.target);\n  }\n\trender() {\n    var listItems = this.state.colors.map((item, i) => {\n      return (\n        <li \n          data-id={i}\n          key={i}\n          draggable=''true''\n          onDragEnd={this.dragEnd.bind(this)}\n          onDragStart={this.dragStart.bind(this)}>{item}</li>\n      )\n     });\n\t\treturn (\n\t\t\t<ul onDragOver={this.dragOver.bind(this)}>\n        {listItems}\n      </ul>\n\t\t)\n\t}\n}\nclass App extends React.Component {\n\tconstructor(props) {\n\t\tsuper(props);\n\t\tthis.state = {\n\t\t\tcolors: [''Red'', ''Green'', ''Blue'', ''Yellow'', ''Black'', ''White'', ''Orange'']\n\t\t}\n\t}\n\trender() {\n\t\treturn (\n\t\t\t<div>\n        <List colors={this.state.colors} />\t\n\t\t\t</div>\n\t\t)\n\t}\n}\n\nReactDOM.render(\n\t<App />,\n    document.getElementById(''app'')\n);"}'),
