('junior', 'List', 'https://git-react-components.s3.amazonaws.com/851d4d0d-dc11-43ee-9d60-2704e2bf36c1-Simple-Dynamic-List.PNG', 'Simple Dynamic List', '{  "html":"<div id=\"wrapper\">\n  <div class=\"container\">\n    <div class=\"row\">\n      <div class=\"col-md-12\">\n        <div id=\"app\" class=\"app-container\">\n        </div>\n      </div>\n    </div>\n  </div>\n</div>",  "scss":"body{\nbackground: #C33764;\nbackground: -webkit-linear-gradient(to top, #1D2671, #C33764);\nbackground: linear-gradient(to top, #1D2671, #C33764);\ncolor: #fff;\n}\nhtml{\n  background: #1D2671;\n}\nul{\n  list-style: none;\n  padding: 0;\n  width: 70%;\n  float:right;\n}\n\nli{\n  width: 100%;\n  text-align: center;\n  background-color: #0d264f;\n  border-radius: 10px;\n  margin: 2px;\n  padding: 5px;\n  font-size: 18px;\n  cursor: pointer;\n}\n\nform{\n  float:left;\n  width: 30%;\n  padding: 20px;\n  background-color: \n}\n\ninput{\n  width: 100%;\n  border: none;\n  padding: 10px;\n  font-size:20px;\n  color: #0d264f;\n  background-color: #c7c7e7;\n}\n\nbutton{\n  float: right;\n  padding: 10px 20px;\n  margin: 10px 0;\n  border: none;\n  border-radius: 3px;\n  color: #fff;\n  background-color: #0d264f;\n}",  "js":"class App extends React.Component {\n  constructor(props){\n    super(props);\n    this.state = {\n      dynamicList: []\n    };\n    this.addListItem = this.addListItem.bind(this);\n    this.removeListItem = this.removeListItem.bind(this);\n  }\n  addListItem(itemToAdd){\n    let currentList = this.state.dynamicList;\n    currentList.push(itemToAdd);\n    this.setState({dynamicList : currentList});\n  }\n  removeListItem(itemToRemove){\n    let currentList = this.state.dynamicList;\n    currentList.splice(itemToRemove, 1);\n    this.setState({dynamicList : currentList});\n  }\n  render(){\n    return(\n      <div class=\"component-wrapper\">\n        <h1>Simple Dynamic List</h1>\n        <DynamicList listItems={this.state.dynamicList} removeItem={this.removeListItem} />\n        <InputBox addItem={this.addListItem} />\n      </div>\n    );\n  }\n}\n\nclass DynamicList extends React.Component {\n  render(){\n    return (\n      <ul>\n        {\n          Object.keys(this.props.listItems).map( (index) => {\n            return (\n              <li onClick={ () => this.props.removeItem(index) } name={index}>{this.props.listItems[index]}</li>\n            );\n          })\n        }\n      </ul>\n    );\n  }\n}\n\nclass InputBox extends React.Component {\n  formSubmit(e){\n    e.preventDefault();    \n    let itemToAdd = this.refs.item.value;\n    if(itemToAdd != ''){\n      this.props.addItem(itemToAdd);\n      this.refs.item.value = '';\n    }\n  }\n  render(){\n    return (\n      <form ref=\"itemForm\" onSubmit={e => this.formSubmit(e)}>\n        <p>Add items to the dynamic list</p>\n        <input type=\"text\" id=\"item\" ref=\"item\"/><br />\n        <button type=\"submit\" class=\"btn btn-primary\">Add Item</button>\n      </form>\n    );\n  }\n}\n\nReact.render(\n  <App />,\n  document.getElementById(''app'')\n);"}'),
('junior', 'List', 'https://git-react-components.s3.amazonaws.com/6f562c72-ffe9-42c0-8cf1-ca1ca6b4bbf9-Simple-List-Render.PNG', 'Simple List Render', '{  "html":"<div id=\"root\"></div>",  "scss":"",  "js":"const listOfFruits = [\"apple\",\"orange\",\"mango\",\"pineapple\"];\n\nclass Something extends React.Component{\n  render() {\n    return(\n      <div>\n        <h2> Revising Lists </h2> \n        {   \n          listOfFruits.map(fruit => (<h3> {fruit}</h3>))      \n        }\n        </div>\n    \n    )\n  }\n}\n\nReactDOM.render(\n  <Something />,\n  document.getElementById(''root'')\n);\n"}'),
('junior', 'Menu', 'https://git-react-components.s3.amazonaws.com/c4bcba91-62a2-43aa-8df3-f2e190009cb0-Buttons-Menu-with-Animation.PNG', 'Buttons Menu w/ Animation', '{  "html":"#root",  "scss":"$accent: #ffa500;\n\n#root {\n  background: #271900;\n  height: 100vh;\n}\n\n// Used by <ButtonGroup />\n.button-group {\n  display: flex;\n  align-items: center;\n  flex-direction: row;\n  flex-wrap: nowrap;\n}\n\n// Used by <Button />\n.button {\n  position: relative;\n  outline: none;\n  background: $accent;\n  width: 40px;\n  height: 40px;\n  border: 0;\n  border-radius: 100px;\n  box-shadow: inset 0 0 0 1px rgba(#000, 0.2), 0 2px 0 rgba($accent, 0.2);\n  margin: 0 12px;\n  color: #FFF;\n  cursor: pointer;\n  \n  &--large {\n    z-index: 1;\n    position: relative;\n    width: 60px;\n    height: 60px;\n  }\n  \n  &:hover {\n    .tooltip {\n      top: -100%;\n      opacity: 1;\n    }\n  }\n}\n\n// Used by inside <Button />\n.icon {\n  top: 50%;\n  left: 50%;\n  transform: translate(-50%, -50%);\n  transition: background 0.3s ease;\n\n  &,\n  &:before,\n  &:after {\n    position: absolute;\n    display: block;\n    background: #FFF;\n    width: 6px;\n    height: 6px;\n    border-radius: 10px;\n  }\n  \n  &:before,\n  &:after {\n    content: '';\n    transition: all 100ms ease,\n      right 100ms 100ms ease,\n      left 100ms 100ms ease;\n  }\n  \n  &:before {\n    left: -11px;\n    transform: translateX(-50%);\n  }\n  \n  &:after {\n    right: -11px;\n    transform: translateX(50%);\n  }\n  \n  &.active {\n    background: transparent;\n    transition: background 0.3s ease;\n    \n    &,\n    &:before,\n    &:after {\n      height: 3px;\n    }\n    \n    &:before,\n    &:after {\n      width: 20px;\n      transform-origin: 50% 50%;\n      transition: all 100ms ease, \n        width 100ms 100ms ease, \n        transform 300ms 200ms cubic-bezier(0.28, 0.55, 0.385, 1.65);\n    }\n    \n    &:before {\n      left: 50%;\n      transform: translateX(-50%) rotate(45deg);\n    }\n    \n    &:after {\n      right: 50%;\n      transform: translateX(50%) rotate(-45deg);\n    }\n  }\n}\n\n// Used by <Tooltip />\n.tooltip {\n  position: absolute;\n  top: -80%;\n  left: 50%;\n  display: inline-block;\n  background: rgba(#000, 0.8);\n  border-radius: 2px;\n  padding: 4px;\n  box-sizing: border-box;\n  transform: translate(-50%, 50%);\n  color: #FFF;\n  font-size: 10px;\n  opacity: 0;\n  transition: 200ms ease;\n}\n\n// Demo Purposes\nbody {\n  background: #F3F3F3;\n}\n\n.container {\n  position: absolute;\n  top: 50%;\n  left: 50%;\n  display: block;\n  align-items: center;\n  flex-direction: row;\n  justify-content: center;\n  flex-wrap: nowrap;\n  margin: 0 auto;\n  transform-origin: center;\n  transform: translate(-50%, -50%) scale(1, 1);\n}",  "js":"const { Component } = React;\nconst { render } = ReactDOM;\nconst { Motion, StaggeredMotion, spring } = ReactMotion;\nconst { noop } = _;\n\n/**\n * <App />\n */\nclass App extends Component {\n  constructor() {\n    super();\n    \n    this.state = {\n      active: false,\n    }\n    \n    this._onClick = this._onClick.bind(this);\n  }\n  \n  _onClick() {\n    this.setState({ \n      active: !this.state.active \n    });\n  }\n\n  render() {\n    const iconArrayOne = [1, 2, 3];\n    const iconArrayTwo = [1, 2, 3].reverse();\n    const tooltipArrayOne = [''One'', ''Two'', ''Three''];\n    const tooltipArrayTwo = [''One'', ''Two'', ''Three''].reverse();\n    \n    return (\n      <div className=\"container\">\n        <ButtonGroup>\n          <StaggeredMotion\n            defaultStyles={[\n              { x: -45, o: 0 },\n              { x: -45, o: 0 },\n              { x: -45, o: 0 },\n            ]}\n            styles={prevInterpolatedStyles => prevInterpolatedStyles.map((_, i) => {\n              return i === prevInterpolatedStyles.length - 1\n                ? {\n                  x: spring(this.state.active ? 0 : -45, { stiffness: 330, damping: 20 }),\n                  o: spring(this.state.active ? 1 : 0, { stiffness: 330, damping: 20 }),\n                } : {\n                  x: spring(prevInterpolatedStyles[i + 1].x, { stiffness: 330, damping: 20 }),\n                  o: spring(prevInterpolatedStyles[i + 1].o, { stiffness: 330, damping: 20 }),\n                };\n            })}\n          >\n            {interpolatingStyles =>\n              <ButtonGroup>\n                {interpolatingStyles.map((style, i) =>\n                  <Button\n                    key={i}\n                    style={{\n                      position: ''relative'',\n                      right: style.x,\n                      opacity: style.o,\n                      pointerEvents: this.state.active ? ''auto'' : ''none'',\n                    }}\n                  >\n                    <Tooltip text={tooltipArrayOne[i]} />\n                    {iconArrayOne[i]}\n                 </Button>\n                )}\n              </ButtonGroup>\n            }\n          </StaggeredMotion>\n          \n          <Motion\n            defaultStyle={{ s: 0.675 }}\n            style={{ s: spring(this.state.active ? 1 : 0.675, { stiffness: 330, damping: 14 }) }}\n          >\n            {interpolatingStyles =>\n              <Button \n                className=\"button--large\" \n                onClick={this._onClick} \n                style={{ \n                  transform: ''scale('' + interpolatingStyles.s + '')'',\n                }}\n              >\n                <span className={this.state.active ? ''icon active'' : ''icon''} />\n              </Button>\n            }\n          </Motion>\n          \n          <StaggeredMotion\n            defaultStyles={[\n              { x: -45, o: 0 },\n              { x: -45, o: 0 },\n              { x: -45, o: 0 },\n            ]}\n            styles={prevInterpolatedStyles => prevInterpolatedStyles.map((_, i) => {\n              return i === 0\n                ? {\n                  x: spring(this.state.active ? 0 : -45, { stiffness: 330, damping: 20 }),\n                  o: spring(this.state.active ? 1 : 0, { stiffness: 330, damping: 20 }),\n                } : {\n                  x: spring(prevInterpolatedStyles[i - 1].x, { stiffness: 330, damping: 20 }),\n                  o: spring(prevInterpolatedStyles[i - 1].o, { stiffness: 330, damping: 20 }),\n                };\n            })}\n          >\n            {interpolatingStyles =>\n              <ButtonGroup>\n                {interpolatingStyles.map((style, i) =>\n                  <Button\n                    key={i}\n                    style={{\n                      position: ''relative'',\n                      left: style.x,\n                      opacity: style.o,\n                      pointerEvents: this.state.active ? ''auto'' : ''none'',\n                    }}\n                  >\n                    <Tooltip text={tooltipArrayTwo[i]} />\n                    {iconArrayTwo[i]}\n                  </Button>\n                )}\n              </ButtonGroup>\n            }\n          </StaggeredMotion>\n        </ButtonGroup>\n      </div>\n    );\n  }\n}\n\n/**\n * <Tooltip />\n */\nconst Tooltip = (props) => <span className=\"tooltip\">{props.text}</span>;\n\n/**\n * <ButtonGroup />\n */\nconst ButtonGroup = (props) => <div className=\"button-group\" style={props.style}>{props.children}</div>;\n\n/**\n * <Button />\n */\nconst Button = (props) => <button className={classNames(''button'', props.className)} style={props.style} onClick={props.onClick || noop}>{props.children}</button>;\n\n/**\n * Target #root and render <App />\n */\nrender(<App />, document.getElementById(''root''));\n"}'),
