('junior', 'Search', 'https://git-react-components.s3.amazonaws.com/248478e5-724f-4181-ae34-239a06d12493-Collapsible-Search-Box-Component.PNG', 'Collapsible Search Box Component', '{  "html":"<div id=\"app\"></div>",  "scss":"@import url(https://fonts.googleapis.com/css?family=Roboto:400,500,700,900)\n\n*\n  font-family: ''Roboto''\n  box-sizing: border-box\n\nhtml,\nbody\n  width: 100%\n  height: 100%\n  margin: 0px\n  padding: 0px\n\nhtml\n  border: 15px solid #000\n  \nh1 \n  margin: 0px\n  padding: 10px\n  color: #333\n  font-size: 26px\n  background: #ddd\n  border-bottom: 1px solid #ccc\n  \nform\n  margin: 10px\n  \n    \n.search-box\n  display: flex\n  margin: 15px 0\n  font-family: Roboto\n  font-size: 18px\n  max-width: 500px\n  min-width: 80px\n  height: 40px\n  transition: all 100ms ease-in-out\n  \n  .textbox\n    min-width: 80%\n    border: 1px solid #bbb\n    border-right: 0\n    border-radius: 3px 0 0 3px\n    \n    input[type=text]\n      width: 100%\n      border: 0\n      padding: 8px 10px\n      font-family: Roboto\n      font-size: 18px\n      font-weight: 500\n      outline: none\n      background: transparent\n      \n    &input[type=text]:focus\n      background: #fff\n  \n  button\n    display: inline-flex\n    border: none\n    background: #42afe3\n    color: #fff\n    padding: 0 10px\n    border-radius: 0 3px 3px 0\n    text-transform: uppercase\n    letter-spacing: 2px\n    font-weight: 600\n    border: 3px solid #42afe3\n    font-size: 14px\n    cursor: pointer\n    \n    .icon\n      display: inline-block\n      background: url(''https://www.google.com.au/images/nav_logo242.png'') no-repeat -107px -55px\n      width: 24px\n      height: 24px\n      margin: 0 auto\n      \n    \n    &:hover\n      background: #4281e3\n      border: 3px solid #4281e3",  "js":"class App extends React.Component {\n  constructor() {\n    super();\n    this.state = {\n      searchboxStyle: {\n        width: ''120px'',\n        background: ''#eee''\n      }\n    }\n    \n    this._handleTextFocus = this._handleTextFocus.bind(this);\n    this._handleClick = this._handleClick.bind(this);\n    this._handleTextBlur = this._handleTextBlur.bind(this);\n  }\n  \n  _handleTextFocus(e) {\n    e.preventDefault();\n    \n    this.setState({\n      searchboxStyle: {\n        width: ''100%'',\n        background: ''#fff''\n      }\n    })\n  }\n  \n  _handleTextBlur(e) {\n    e.preventDefault();\n    if (!this._searchBox.value && !this._searchBox.value.length > 0) \n      this.setState({\n        searchboxStyle: {\n          width: ''120px'',\n          background: ''#eee''\n        }\n      })\n  }\n  \n  _handleClick(e) {\n    e.preventDefault();\n  }\n  \n  render() {\n    return (\n      <div>\n        <h1>Search Form</h1>\n        <form onSubmit={this._handleClick}>\n          <div className=\"search-box\"\n            style={this.state.searchboxStyle}>\n            <div className=\"textbox\">\n              <input type=\"text\" \n                name=\"search\" \n                ref={f => this._searchBox = f}\n                placeholder=\"Search\"\n                autoComplete=\"off\"\n                onFocus={this._handleTextFocus}\n                onBlur={this._handleTextBlur}\n                className=\"auto-expand\" />\n            </div>\n            <button className=\"search-button\">\n              <span className=\"icon\"></span>\n            </button>\n          </div>\n        </form>\n      </div>\n    );\n  }\n}\n\nReactDOM.render(<App />, document.getElementById(''app''));"}'),
